import Camera_icon from '../assets/profile/camera-icon.png';
import Pencil_icon from '../assets/profile/pencil.png';
import './ProfileCard.css';
import Profile from '../assets/profile.jpeg';
import TryPremium from './items/TryPremium';
import { BiDownArrowAlt, BiGroup, BiSave, BiSend, BiUpArrowAlt } from 'react-icons/bi';
import { CgEventbrite } from 'react-icons/cg';
import { useState, useEffect } from 'react';
import axiosInstance from '../api/axiosInstance';
import classNames from 'classnames';

const ProfileCard = ({main}) => {
    const [isMore, setIsMore] = useState(false);

    const [profile,setProfile]= useState({});
    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await axiosInstance.get('/profile');
                setProfile(response.data);
            } catch (error) {
                console.error('Error fetching profile:', error);
            }
        };

        fetchProfile();
    }, []);

    const toggleIsMore = () => {
        setIsMore(!isMore);
    };


    if (!profile) {
        return <div>Loading...</div>;
    }

    return (
        <div className={`profile-section ${main ? 'more-profile-section' : ''} `}>
            <div className={`profile-container ${main ? 'more-profile-container' : ''}`}>
                <div className="bg">
                    <img className="profile-image" src={`http://localhost:3001/${profile.profileImageUrl}`} alt="Profile" />
                    {main && <button>

                        <img src={Camera_icon}/>
                    </button>}
                </div>

                <div className={`white-space ${main ? 'more-white-space' : ''}`}>
                    {main && <button>
                        <img src={Pencil_icon}/>
                    </button>}
                </div>
                <div className='profile-info'>
                    <h1>{profile.name}</h1>
                    <p>{profile.about || 'About not provided'}</p>
                    <div className='location-contact-profile-info'>
                        <span>{profile.contactInfo?.location || 'Location not provided'}</span>
                        <a href='#'>Contact info</a>
                    </div>
                    {/* connections */}
                    {main && 
                    <span className='connections-user-profile'>19 connections</span>}

                    {/* Open to section */}
                    <div className='opento-section-user-profile'>

                        <div className='opento-item-user-profile weight'>
                            <span>Open to</span>
                        </div>
                        <div className='opento-item-user-profile'>
                            <span>Add profile section</span>
                        </div>
                        <div className='opento-item-user-profile'>
                            <span>Enhance profile</span>
                        </div>
                        <div className='opento-item-user-profile relative extend'>
                            <span>More</span>
                            <div className='absolute bottom-absolute flex flex-gp10'>
                                <span className='flex flex-gp5'><BiSend className='svg-30'></BiSend><span className='sx-text'>send profile in messages</span></span>
                            </div>
                        </div>

                    </div>
                    {/* Open to work box */}
                    <div className='open-to-work-box-user-profile'>
                        <span className='heading'>Open to work</span>
                        <span className='short-content'>Web Developer, Back End Developer, FUll-stack Developer and Frontend Developer roles</span>
                        <span className='link'>Show details</span>
                        <span className='edit-icon'><img src={Pencil_icon}/></span>
                    </div>
                    {profile.contactInfo?.company && 
                    <div className='company'>
                        <div className='company-default'>
                            {/* Additional company info if needed */}
                        </div>

                        <span>{profile.contactInfo?.company} </span>

                    </div>
                    }
                </div>
            </div>
            {/* Premium suggestion */}
            <div style={{ display: isMore ? 'flex' : 'none' }} className='premium-suggestion'>
                <span>Achieve 4x more profile visits</span>
                <TryPremium isVertical={false} />
            </div>

            {/* Last item */}
            <div style={{ display: isMore ? 'flex' : 'none' }} className='profile-more-info'>
                <div className='profile-network'>
                    <div className='profile-connections'>
                        <span>Connections</span>
                        <span className='connections'>{profile.connections}</span>
                    </div>
                    <span>Grow your network</span>
                </div>
                <hr />
                <div className='more-items'>
                    {/* Saved items */}
                    <div className='more-item'>
                        <BiSave />
                        <span>Saved items</span>
                    </div>
                    {/* Groups */}
                    <div className='more-item'>
                        <BiGroup />
                        <span>Groups</span>
                    </div>
                    {/* Events */}
                    <div className='more-item'>
                        <CgEventbrite />
                        <span>Events</span>
                    </div>
                </div>
            </div>

            {/* more - less */}
            {!main && 
            <div onClick={toggleIsMore} className='show-more'>
                {!isMore &&
                    <div className='show'>
                        <span>Show more</span>
                        <BiDownArrowAlt />
                    </div>
                }

                {/* Hide */}
                {isMore &&
                    <div className='hide'>
                        <span>Show less</span>
                        <BiUpArrowAlt />
                    </div>
                }
            </div>
            }
        </div>
    );
}

export default ProfileCard;
