import React, { createContext, useState, useEffect } from 'react';
import axiosInstance from '../api/axiosInstance';

const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [member, setMember] = useState(null);

  useEffect(() => {
    const checkUser = async () => {
      try {
        const res = await axiosInstance.get('/auth/user');
        setUser(res.data);
      } catch (err) {
        console.error(err);
        handleAuthError(err); // Handle token expiry or unauthorized errors
      } finally {
        setLoading(false);
      }
    };
    checkUser();
  }, []);

  const login = async (email, password) => {
    try {
      const res = await axiosInstance.post('/api/auth/login', { email, password });
      setUser(res.data.user);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', res.data.user.id);
      return res.data;
    } catch (err) {
      console.error(err);
      throw err;
    }
  };

  const logout = () => {
    try {
      setUser(null);
      localStorage.removeItem('token');
      localStorage.removeItem('user');
    } catch (err) {
      console.error(err);
    }
  };


  // Function to handle authentication errors (e.g., token expiry)
  const handleAuthError = (error) => {
    if (error.response && error.response.status === 401) {
      logout(); // Clear user state and token
      // Redirect to login page or display a notification to the user
      // Example: window.location.href = '/login';
    }
  };

  return (
    <AuthContext.Provider value={{ user , login, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );

};

export { AuthContext, AuthProvider };
