import logo from './logo.svg';
import './App.css';
import Home from './pages/Home';
import ProfileCard from './components/ProfileCard';
import { UserContext } from './UserContext';
import { useContext } from 'react';
import { BrowserRouter , Router , Route, Routes, Redirect} from 'react-router-dom';
import InitialHome from './pages/InitialHome';
import LoginPage from './pages/LoginPage';

function App() {
  const {user,isPremium} = useContext(UserContext);
  return (

    <div className="App">
    <BrowserRouter>
      <Routes>
        <Route path="/login" component={LoginPage} />
        <Route path="/unauthorized" component={InitialHome} />
        <Route path="/" render={() => (user ? <Home/> : <Redirect to="/login" />)} />
      </Routes>
    </BrowserRouter>
     {/* <Home/> */}
    </div>
  );
}

export default App;
