import React, { useState } from 'react';
import axiosInstance from '../api/axiosInstance';
import { useNavigate } from 'react-router-dom'; // If you want to redirect after login

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null); // State to handle error messages
    const history = useNavigate(); // Hook for redirecting

    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent default form submission
        try {
            const result = await axiosInstance.post('/auth/login', { email, password });
            // Handle successful login, e.g., set user context, redirect, etc.
            history('/'); // Redirect to home page or another route
        } catch (err) {
            setError('Login failed. Please check your credentials and try again.'); // Set error message
            console.error('Login error:', err);
        }
    };

    return (
        <div className="login">
            <h1>Login Page</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error message */}
            <p>Don't have an account? <a href="/register">Register</a></p>
        </div>
    );
};

export default LoginPage;
